import { Component } from "react";
import Main from '../../layouts/Dashboard/Main';

class HistoryView extends Component {

    constructor(props){
        super(props);
        this.state = {
            userHistoryAll: null,
            userHistoryLimited : null,
            pagination: {
                start: 1,
                end: 10
            }
        }
    }

    componentDidMount(){
        this.apiHistoryLimited(1, 10);
        this.apiHistoryAll();
    }

    async pagination(type, start , end){
        if(type === 'increment'){
            if(start >= this.state.userHistoryAll.length){
                await alert('Data Maksimal');
            }else{
                this.setState({
                    pagination:{
                        start: start + 10,
                        end: end + 10
                    }
                })
            }
        }else{
            this.setState({
                pagination:{
                    start: start - 10,
                    end: end - 10
                }
            })
        }
    }


    apiHistoryAll(){
        const currentUrl = window.location.href;
        const uriSegment = currentUrl.split('/');
        const host = uriSegment[2];

        const apiUrl = `http://${host}/api/user/info?type=history`;
        fetch(apiUrl, {
            method: 'get',
            headers: {
                'Authorization': `Bearer ${this.props.token}`
            }
        }).then(res => res.json()).then(res => {
            this.setState({
                userHistoryAll: res.data
            })
        })
    }

    async apiHistoryLimited(start, end){
        const currentUrl = window.location.href;
        const uriSegment = currentUrl.split('/');
        const host = uriSegment[2];

        const apiUrl = `http://localhost:3000/api/user/info?type=history&start=${start}&end=${end}`;
        await fetch(apiUrl, {
            method: 'get',
            headers: {
                'Authorization': `Bearer ${this.props.token}`
            }
        }).then(res => res.json()).then(res => {
            this.setState({
                userHistoryLimited: res.data
            })
        })
    }

    render(){
        return(
            <div>
                <Main token={this.props.token}>
                <div className="col-lg-12 col-md-12">
                    <div className="card">
                        <div className="card-header card-header-warning">
                        <h4 className="card-title">History Transaksi</h4>
                        <p className="card-category"></p>
                        </div>
                        <div className="card-body table-responsive">
                        <table className="table table-hover">
                            <thead className="text-warning">
                            <th>Kode Produk</th>
                            <th>Nama Produk</th>
                            <th>Harga</th>
                            <th>Tujuan</th>
                            <th>Detail</th>
                            </thead>
                            <tbody>
                            
                                { this.state.userHistoryLimited ?
                                    this.state.userHistoryLimited.map(data => {
                                        return(
                                            <tr>
                                                <td>1</td>
                                                <td>Dakota Rice</td>
                                                <td>$36,738</td>
                                                <td>Niger</td>
                                                <td><a href='#'>Lihat</a></td>
                                            </tr>
                                        )
                                    })
                                    :
                                    null
                                }
                                
                            </tbody>
                        </table>

                        <nav aria-label="...">
                            <ul className="pagination">
                                <li className="page-item ">
                                <a className="page-link" onClick={() => {
                                    this.pagination('dec', this.state.pagination.start, this.state.pagination.end)
                                }} href="#" tabindex="-1" aria-disabled="true">Previous</a>
                                </li>
                                <li className="page-item">
                                <a className="page-link" onClick={() => {
                                    this.pagination('increment', this.state.pagination.start, this.state.pagination.end)
                                }} href="#">Next</a>
                                </li>
                            </ul>
                        </nav>
                        </div>
                    </div>
                    </div>
                </Main>
            </div>
        )
    }

}

export default HistoryView;
