import ArgonMainHeader from '../../layouts/ArgonMainHeader';
import ArgonMainBody from '../../layouts/ArgonMainBody';

class RegisterModal extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            register: {
                email: '',
                pass: '',
                passConfirm: ''
            }
        }
    }

    onHandleChange(to, e){
        this.setState({
            register: {
                [to] : e.target.value
            }
        });
        console.log(e.target.value);
    }

    render(){
        return(
                <div className="modal fade" id="registerModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div className="modal-dialog modal-dialog-centered" role="document">
                        <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Buat akun baru!</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div className="modal-body">

                        <div className="form-group mb-3">
                            <div className="input-group input-group-merge input-group-alternative">
                                <div className="input-group-prepend">
                                <span className="input-group-text"><i className="ni ni-email-83"></i></span>
                                </div>
                                <input name='email' className="form-control" placeholder="Email" type="email" required/>
                            </div>
                            <div className="form-group">
                            </div>
                            <div className="input-group input-group-merge input-group-alternative">
                                <div className="input-group-prepend">
                                <span className="input-group-text"><i className="ni ni-lock-circle-open"></i></span>
                                </div>
                                <input name="password" className="form-control" placeholder="Password" type="password" required/>
                            </div>
                            <div className="form-group">
                            </div>
                            <div className="input-group input-group-merge input-group-alternative">
                                <div className="input-group-prepend">
                                <span className="input-group-text"><i className="ni ni-lock-circle-open"></i></span>
                                </div>
                                <input name="password-confirm" className="form-control" placeholder="Konfirmasi Password" type="password" required/>
                            </div>
                        </div>
                            
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Tutup</button>
                            <button type="button" onClick={() => {

                            }} className="btn btn-primary">Buat Akun</button>
                        </div>
                        </div>
                    </div>
                </div>
        )
    }

}

export default RegisterModal;
