import { Component } from "react";
import Main from '../../layouts/Dashboard/Main';

class HistoryView extends Component {

    constructor(props){
        super(props);
        this.state = {
            userDataLogin : null
        }
    }

    componentDidMount(){
        this.apiDataLogin();
    }

    apiDataLogin(){
        const currentUrl = window.location.href;
        const uriSegment = currentUrl.split('/');
        const host = uriSegment[2];

        const apiUrl = `http://${host}/api/user/info?type=history`;
        fetch(apiUrl, {
            method: 'get',
            headers: {
                'Authorization': `Bearer ${this.props.token}`
            }
        }).then(res => res.json()).then(res => {
            console.log(res)
        })
    }

    render(){
        return(
            <div>
                <Main token={this.props.token}>
                <div className="col-lg-12 col-md-12">
                    <div className="card">
                        <div className="card-header card-header-warning">
                        <h4 className="card-title">History Transaksi</h4>
                        <p className="card-category"></p>
                        </div>
                        <div className="card-body table-responsive">
                        <table className="table table-hover">
                            <thead className="text-warning">
                            <th>Kode Produk</th>
                            <th>Nama Produk</th>
                            <th>Harga</th>
                            <th>Tujuan</th>
                            <th>Detail</th>
                            </thead>
                            <tbody>
                            <tr>
                                <td>1</td>
                                <td>Dakota Rice</td>
                                <td>$36,738</td>
                                <td>Niger</td>
                                <td>Lihat</td>
                            </tr>
                            </tbody>
                        </table>

                        <nav aria-label="...">
                            <ul className="pagination">
                                <li className="page-item ">
                                <a className="page-link" href="#" tabindex="-1" aria-disabled="true">Previous</a>
                                </li>
                                <li className="page-item">
                                <a className="page-link" href="#">Next</a>
                                </li>
                            </ul>
                        </nav>
                        </div>
                    </div>
                    </div>
                </Main>
            </div>
        )
    }

}

export default HistoryView;
