import Loader from 'react-loader-spinner';

class ProductModal extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            userSelect: {
                numberTarget: '',
                productTarget: ''
            },
            listProduct: null
        }
    }

    componentDidMount(){
        
    }

   apiPpobRequest(provaider, type){
        const apiUrl = 'http://localhost:3000/api/product/pulsa';
        fetch(apiUrl,{
            method: 'get',
            headers: {
                "Authorization": `Bearer ${this.props.token}`
            }
        }).then(res => res.json()).then(res => {
            
            this.setState({
                listProduct: res.data
            })
        }).then(console.log(this.state.listProduct))
   }

   onChangeNumber(number){
    const three = ['0895', '0896', '0897', '0898', '0899'];
    const smart = ['0881', '0882', '0883', '0884', '0885', '0886', '0887', '0888', '0889'];
    const axis = ['0838', '0831', '0832', '0833'];
    const tsel = ['0811', '0812', '0813', '0821', '0822', '0852', '0853', '0823', ];
    const xl = ['0817', '0818', '0819', '0859', '0877', '0878'];
    const isat = ['0814', '0815', '0816', '0855', '0856', '0857', '0858'];
    const byU = ['0851'];

    const frontNumb = number.target.value.slice(0, 4)

        this.setState({
            userSelect: {
                numberTarget: number.target.value
            }
        })
        
        if(three.includes(frontNumb)){
            this.apiPpobRequest(3, 'pulsa');
        }else if(smart.includes(frontNumb)){
            this.apiPpobRequest(5, 'pulsa');
        }else if(xl.includes(frontNumb)){
            this.apiPpobRequest(6);
        }else if(tsel.includes(frontNumb)){
            this.apiPpobRequest(1);
        }else if(isat.includes(frontNumb)){
            this.apiPpobRequest(2);
        }else if(axis.includes(frontNumb)){
            this.apiPpobRequest(4);
        }else if(byU.includes(frontNumb)){
            this.apiPpobRequest(7);
        }
   }

   onChangeSelect(){

   }

    render(){
        return(
                <div className="modal fade" id="productModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div className="modal-dialog modal-dialog-centered" role="document">
                        <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">{this.props.type}</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                            </button>

                        </div>
                        <div className="modal-body">
                            <div className="form-group mb-3">
                                <div className="input-group input-group-merge input-group-alternative">
                                    <div className="input-group-prepend">
                                    <span className="input-group-text"><i className="ni ni-email-83"></i></span>
                                    </div>
                                    <input value={this.state.userSelect.numberTarget} className="form-control" onChange={(e) => {
                                        this.onChangeNumber(e)
                                    }} placeholder="No Tujuan" type="number" required/>
                                </div>
                            </div>

                            <div className="form-group">
                            <div className="input-group input-group-merge input-group-alternative">
                                    <div className="input-group-prepend">
                                    <span className="input-group-text"><i className="ni ni-email-83"></i></span>
                                    </div>
                                        <select value={this.state.userSelect.productTarget} onChange={(e) => {
                                            this.onChangeSelect(e);
                                        }} class="form-control">
                                            
                                        </select>
                                </div>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Tutup</button>
                            <button type="button" onClick={() => {
                                console.log(this.state.userSelect.typeProduct);
                            }} className="btn btn-primary">Beli</button>
                        </div>
                        </div>
                    </div>
                    
                    <div className="fixed-bottom d-flex justify-content-center">
                        <Loader type="ThreeDots" color="#ffff00" height={70} width={70} />
                    </div>
                </div>
        )
    }

}

export default ProductModal;
