import { Component } from "react";
import Main from '../../layouts/Dashboard/Main';
import Link from 'next/link';

class HistoryView extends Component {

    constructor(props){
        super(props);
        this.state = {
            userHistory : null,
            pagination: {
                start: 1,
                end: 5
            }
        }
    }

    componentDidMount(){
        this.apiDataLogin(1, 5);
    }

    pagination(type){
        if(type === 'increment'){

        }
        const router = useRouter();
        router.push('/dashboard/home');
    }

    apiDataLogin(start, end){
        const currentUrl = window.location.href;
        const uriSegment = currentUrl.split('/');
        const host = uriSegment[2];

        const apiUrl = `http://${host}/api/user/info?type=history&start=${start}&end${end}`;
        fetch(apiUrl, {
            method: 'get',
            headers: {
                'Authorization': `Bearer ${this.props.token}`
            }
        }).then(res => res.json()).then(res => {
            this.setState({
                userHistory: res.data
            })
        })
    }

    render(){
        return(
            <div>
                <Main token={this.props.token}>
                <div className="col-lg-12 col-md-12">
                    <div className="card">
                        <div className="card-header card-header-warning">
                        <h4 className="card-title">History Transaksi</h4>
                        <p className="card-category"></p>
                        </div>
                        <div className="card-body table-responsive">
                        <table className="table table-hover">
                            <thead className="text-warning">
                            <th>Kode Produk</th>
                            <th>Nama Produk</th>
                            <th>Harga</th>
                            <th>Tujuan</th>
                            <th>Detail</th>
                            </thead>
                            <tbody>
                            <tr>
                                <td>1</td>
                                <td>Dakota Rice</td>
                                <td>$36,738</td>
                                <td>Niger</td>
                                <td><a href='#'>Lihat</a></td>
                            </tr>
                            </tbody>
                        </table>

                        <nav aria-label="...">
                            <ul className="pagination">
                                <li className="page-item ">
                                <a className="page-link" onClick={() => {
                                    <Link href="">
                                    </Link>
                                }} href="#" tabindex="-1" aria-disabled="true">Previous</a>
                                </li>
                                <li className="page-item">
                                <a className="page-link" onClick={() => {
                                    this.pagination('increment');
                                }} href="#">Next</a>
                                </li>
                            </ul>
                        </nav>
                        </div>
                    </div>
                    </div>
                </Main>
            </div>
        )
    }

}

export default HistoryView;
